--[[
	MapSetup_CommandBar.lua
	
	Paste each STEP into the Roblox Studio Command Bar one at a time, pressing Enter after each.
	
	STEP 1: Create habitats (zones)
	STEP 2: Create Marlow the Sea Otter (Kelp Forest)
	STEP 3: Create Luna the Sea Lion (Rocky Outcrop)
	STEP 4: Create Seahorse (Pier)
	STEP 5: Create Red Octopus (Rocky Crevice)
	STEP 6: Create Treefish (Sandy Bottom)
	STEP 7: Add effects (glow, particles, sparkles) to all creatures
]]


------------------------------------------------------------------------
-- STEP 1: Habitats (paste this line into Command Bar)
------------------------------------------------------------------------
local e=workspace:FindFirstChild("Habitats") if e then e:Destroy() end local h=Instance.new("Folder") h.Name="Habitats" h.Parent=workspace local zones={{n="KelpForest",p=Vector3.new(50,2,50),s=Vector3.new(80,4,80),c=Color3.fromRGB(34,139,34),m=Enum.Material.Grass},{n="RockyOutcrop",p=Vector3.new(-60,4,30),s=Vector3.new(60,8,60),c=Color3.fromRGB(128,128,128),m=Enum.Material.Slate},{n="SandyBottom",p=Vector3.new(0,0,-80),s=Vector3.new(100,2,80),c=Color3.fromRGB(237,201,175),m=Enum.Material.Sand},{n="Pier",p=Vector3.new(-30,3,-30),s=Vector3.new(20,6,80),c=Color3.fromRGB(139,90,43),m=Enum.Material.Wood},{n="RockyCrevice",p=Vector3.new(80,3,-40),s=Vector3.new(50,12,50),c=Color3.fromRGB(80,80,80),m=Enum.Material.Rock}} for _,v in zones do local z=Instance.new("Part") z.Name=v.n z.Size=v.s z.Position=v.p z.Color=v.c z.Material=v.m z.Anchored=true z.CanCollide=true z.Transparency=0.3 z.Parent=h local bb=Instance.new("BillboardGui") bb.Size=UDim2.fromOffset(200,50) bb.StudsOffset=Vector3.new(0,v.s.Y/2+3,0) bb.AlwaysOnTop=true bb.Parent=z local lb=Instance.new("TextLabel") lb.Size=UDim2.fromScale(1,1) lb.BackgroundTransparency=1 lb.Text=v.n lb.TextColor3=Color3.new(1,1,1) lb.TextStrokeTransparency=0 lb.TextScaled=true lb.Font=Enum.Font.GothamBold lb.Parent=bb end print("[MapSetup] Step 1 done: 5 habitat zones created!")


